// src/App.js
import React, { useEffect, useState, useRef } from "react";

const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:4000";

function isValidEmail(email) {
  return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
}

function isValidPhone(phone) {
  // basic 10-digit validation (India-style). Modify if needed.
  return /^[0-9]{10}$/.test(phone);
}

export default function App() {
  const [candidates, setCandidates] = useState([]);
  const [loading, setLoading] = useState(false);

  const [search, setSearch] = useState("");
  const [form, setForm] = useState({
    name: "",
    email: "",
    phone: "",
    jobTitle: "",
  });
  const resumeInputRef = useRef(null);
  const [resumeFile, setResumeFile] = useState(null);

  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchCandidates();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  async function fetchCandidates() {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(`${API_BASE}/candidates`);
      if (!res.ok) throw new Error("Failed to fetch candidates");
      const data = await res.json();
      setCandidates(data);
    } catch (err) {
      console.error(err);
      setError("Could not load candidates. Check backend.");
    } finally {
      setLoading(false);
    }
  }

  function handleFormChange(e) {
    const { name, value } = e.target;
    setForm((p) => ({ ...p, [name]: value }));
  }

  function handleFileChange(e) {
    const f = e.target.files[0];
    if (!f) {
      setResumeFile(null);
      return;
    }
    // Accept only PDF by MIME or extension
    const isPdf = f.type === "application/pdf" || f.name.toLowerCase().endsWith(".pdf");
    if (!isPdf) {
      setMessage(null);
      setError("Please upload PDF files only.");
      e.target.value = null;
      setResumeFile(null);
      return;
    }
    setError(null);
    setResumeFile(f);
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setMessage(null);
    setError(null);

    // Basic client-side validation
    if (!form.name.trim() || !form.email.trim() || !form.phone.trim() || !form.jobTitle.trim()) {
      setError("Please fill all required fields.");
      return;
    }
    if (!isValidEmail(form.email.trim())) {
      setError("Enter a valid email address.");
      return;
    }
    if (!isValidPhone(form.phone.trim())) {
      setError("Enter a valid 10-digit phone number.");
      return;
    }

    const fd = new FormData();
    fd.append("name", form.name.trim());
    fd.append("email", form.email.trim());
    fd.append("phone", form.phone.trim());
    fd.append("jobTitle", form.jobTitle.trim());
    if (resumeFile) fd.append("resume", resumeFile);

    try {
      const res = await fetch(`${API_BASE}/candidates`, {
        method: "POST",
        body: fd,
      });

      const body = await res.json();
      if (!res.ok) {
        setError(body.error || "Failed to refer candidate");
        return;
      }

      // Prepend new candidate into list
      setCandidates((p) => [body, ...p]);
      setMessage("Candidate referred successfully!");
      setForm({ name: "", email: "", phone: "", jobTitle: "" });
      setResumeFile(null);
      if (resumeInputRef.current) resumeInputRef.current.value = null;
    } catch (err) {
      console.error(err);
      setError("Server error while referring candidate.");
    }
  }

  async function updateStatus(id, newStatus) {
    setError(null);
    try {
      const res = await fetch(`${API_BASE}/candidates/${encodeURIComponent(id)}/status`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ status: newStatus }),
      });
      if (!res.ok) {
        const b = await res.json().catch(() => ({}));
        throw new Error(b.error || "Failed to update status");
      }
      const updated = await res.json();
      setCandidates((prev) => prev.map((c) => (c.id === id ? updated : c)));
      setMessage("Status updated.");
    } catch (err) {
      console.error(err);
      setError("Could not update status.");
    }
  }

  // Simple search by jobTitle or status (case-insensitive)
  const filtered = candidates.filter((c) => {
    const s = search.trim().toLowerCase();
    if (!s) return true;
    return (
      (c.jobTitle || "").toLowerCase().includes(s) ||
      (c.status || "").toLowerCase().includes(s)
    );
  });

  return (
    <div style={styles.container}>
      <h1 style={styles.h1}>Candidate Referral Dashboard</h1>

      <div style={styles.layout}>
        <aside style={styles.formCard}>
          <h2 style={styles.h2}>Refer a Candidate</h2>

          <form onSubmit={handleSubmit}>
            <label style={styles.label}>
              Name *
              <input
                name="name"
                value={form.name}
                onChange={handleFormChange}
                placeholder="Candidate name"
                style={styles.input}
              />
            </label>

            <label style={styles.row}>
              <div style={{ flex: 1, marginRight: 8 }}>
                <div style={styles.labelSmall}>Email *</div>
                <input
                  name="email"
                  value={form.email}
                  onChange={handleFormChange}
                  placeholder="email@example.com"
                  style={styles.input}
                />
              </div>
              <div style={{ flex: 1 }}>
                <div style={styles.labelSmall}>Phone *</div>
                <input
                  name="phone"
                  value={form.phone}
                  onChange={handleFormChange}
                  placeholder="10 digit phone"
                  style={styles.input}
                />
              </div>
            </label>

            <label style={styles.label}>
              Job Title *
              <input
                name="jobTitle"
                value={form.jobTitle}
                onChange={handleFormChange}
                placeholder="Frontend / Backend / Fullstack"
                style={styles.input}
              />
            </label>

            <label style={styles.label}>
              Resume (optional, PDF only)
              <input
                ref={resumeInputRef}
                type="file"
                accept=".pdf,application/pdf"
                onChange={handleFileChange}
                style={{ marginTop: 8 }}
              />
            </label>

            <div style={{ marginTop: 12 }}>
              <button type="submit" style={styles.button}>
                Refer Candidate
              </button>
            </div>
          </form>

          {message && <div style={styles.message}>{message}</div>}
          {error && <div style={styles.error}>{error}</div>}
        </aside>

        <main style={styles.listCard}>
          <div style={styles.headerRow}>
            <h2 style={styles.h2}>Referrals</h2>
            <input
              placeholder="Search by job title or status"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              style={styles.searchInput}
            />
          </div>

          {loading ? (
            <div style={{ padding: 16 }}>Loading...</div>
          ) : filtered.length === 0 ? (
            <div style={{ padding: 16 }}>No candidates found.</div>
          ) : (
            filtered.map((c) => (
              <div key={c.id} style={styles.card}>
                <div style={{ display: "flex", justifyContent: "space-between" }}>
                  <div>
                    <div style={{ fontWeight: 700 }}>{c.name}</div>
                    <div style={{ fontStyle: "italic", color: "#333" }}>{c.jobTitle}</div>
                    <div style={{ fontSize: 13, color: "#555" }}>
                      {c.email} • {c.phone}
                    </div>
                  </div>

                  <div style={{ textAlign: "right" }}>
                    <div style={{ marginBottom: 8 }}>
                      <div style={{ fontSize: 13 }}>Status</div>
                      <select
                        value={c.status || "Pending"}
                        onChange={(e) => updateStatus(c.id, e.target.value)}
                        style={styles.select}
                      >
                        <option>Pending</option>
                        <option>Reviewed</option>
                        <option>Hired</option>
                      </select>
                    </div>

                    {c.resumeUrl && (
                      <div style={{ marginTop: 8 }}>
                        <a
                          href={`${API_BASE}${c.resumeUrl}`}
                          target="_blank"
                          rel="noreferrer"
                        >
                          View Resume
                        </a>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ))
          )}
        </main>
      </div>
    </div>
  );
}

